package app;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int days = Integer.parseInt(scanner.nextLine());        int chef = Integer.parseInt(scanner.nextLine());        int torti = Integer.parseInt(scanner.nextLine());        int gofreti = Integer.parseInt(scanner.nextLine());        int putki = Integer.parseInt(scanner.nextLine());        double TortiPrice = torti * 45.00;        double GofretiPrice = gofreti * 5.80;        double PutkiPrice = putki * 3.20;        double daysum = (TortiPrice + GofretiPrice + PutkiPrice) * chef;        double total = daysum * days;        double price = total - (total / 8);        System.out.printf("%.2f", price);    }}