package com.company;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int n = Integer.parseInt(scanner.nextLine());        for (int a = 1; a <= n + 1; a++) {            for (int b = 1; b <= n + 1 - a; b++) {                System.out.print(" ");            }            for (int b = 1; b < a; b++) {                System.out.print("*");            }            System.out.print(" | ");            for (int b = 1; b <= a - 1; b++) {                System.out.print("*");            }            System.out.println();        }    }}