package app;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int x = Integer.parseInt(scanner.nextLine());        int y = Integer.parseInt(scanner.nextLine());        int z = Integer.parseInt(scanner.nextLine());        double hourse = z * 8;        double result = y * hourse / 5;        int i = (int) result;        double cups = x - i;        double price = Math.abs(cups * 4.2);        if (i > x) {            System.out.printf("%.2f extra money", price);        } else if (i < x) {            System.out.printf("Losses: %.2f", price);        }    }}