package com.company;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scan = new Scanner(System.in);        int n = Integer.parseInt(scan.nextLine());        int tire = (n - 2) / 2;        int star = n - 4;        if (n % 2 == 0) {            for (int i = 0; i < n / 2; i++) {                System.out.println(repeatStr(tire) + "*" + repeatStr(2 * i) + "*" + repeatStr(tire));                tire--;            }            tire = 1;            for (int i = 0; i < (n / 2) - 1; i++) {                System.out.println(repeatStr(tire) + "*" + repeatStr(star) + "*" + repeatStr(tire));                star -= 2;                tire++;            }        } else if (n > 1) {            System.out.println(repeatStr((n - 1) / 2) + "*" + repeatStr((n - 1) / 2));            for (int i = 0; i < n / 2; i++) {                System.out.println(repeatStr(tire) + "*" + repeatStr(2 * i + 1) + "*" + repeatStr(tire));                tire--;            }            for (int i = 0; i < (n / 2) - 1; i++) {                System.out.println(repeatStr(i + 1) + "*" +                        repeatStr(star) + "*" + repeatStr(i + 1));                star -= 2;                tire++;            }            System.out.println(repeatStr((n - 1) / 2) + "*" + repeatStr((n - 1) / 2));        } else {            System.out.println(repeatStr((n - 1) / 2) + "*" + repeatStr((n - 1) / 2));            for (int i = 0; i < n / 2; i++) {                System.out.println(repeatStr(tire) + "*" + repeatStr(2 * i + 1) + "*" + repeatStr(tire));                tire--;            }            for (int i = 0; i < (n / 2) - 1; i++) {                System.out.println(repeatStr(i + 1) + "*" + repeatStr(star) + "*" + repeatStr(i + 1));                star -= 2;                tire++;            }        }    }    private static String repeatStr(int count) {        StringBuilder sb = new StringBuilder();        for (int i = 0; i < count; i++) {            sb.append("-");        } return sb.toString();    }}