package app;        import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scan = new Scanner(System.in);        int n = Integer.parseInt(scan.nextLine());        int num = n;        int d3 = num % 10;        num /= 10;        int d2 = num % 10;        num /= 10;        int d1 = num % 10;        for (int i = 0; i < d1 + d2; i++) {            for (int j = 0; j < d1 + d3; j++) {                if (n % 5 == 0) {                    n -= d1;                    System.out.printf("%d ",n);                }else if (n % 3 == 0) {                    n -= d2;                    System.out.printf("%d ",n);                }else {                    n += d3;                    System.out.printf("%d ",n);                }            }            System.out.println();        }    }}