package com.company;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int n = Integer.parseInt(scanner.nextLine());        int sum = 0;        int maxdiff = 0;        int lastsum = 0;        int counter = 0;        for (int i = 1; i <= n ; i++) {            int num1 = Integer.parseInt(scanner.nextLine());            int num2 = Integer.parseInt(scanner.nextLine());            sum = num1 + num2;            if (maxdiff < Math.abs(lastsum - sum) && i >= 2) {                maxdiff = Math.abs(lastsum - sum);            }            if (sum == lastsum) {                counter++;            }            if (sum != lastsum) {                lastsum = sum;            }        }        if (counter == n - 1) {            System.out.println("Yes, value=" + sum);        } else {            System.out.println("No, maxdiff=" + maxdiff);        }    }}