package app;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String fruit = scanner.nextLine();        String size = scanner.nextLine();        int n = Integer.parseInt(scanner.nextLine());        double price = 1;        if (size.equals("small")) {            if (fruit.equals("Watermelon")) {                price = 56 * n * 2;            }            if (fruit.equals("Mango")) {                price = 36.66 * n * 2;            }            if (fruit.equals("Pineapple")) {                price = 42.10 * n * 2;            }            if (fruit.equals("Raspberry")) {                price = 20 * n * 2;            }        }        else if (size.equals("big")) {                if (fruit.equals("Watermelon")) {                    price = 28.70 * n * 5;                }                if (fruit.equals("Mango")) {                    price = 19.60 * n * 5;                }                if (fruit.equals("Pineapple")) {                    price = 24.80 * n * 5;                }                if (fruit.equals("Raspberry")) {                    price = 15.20 * n * 5;                }            }        if (price > 1000) {            System.out.printf("%.2f lv.", price - price * 0.5);        } else if (price >= 400 && price <= 1000){            System.out.printf("%.2f lv.", price - price * 0.15);        } else {            System.out.printf("%.2f lv.", price);        }    }}