package app;        import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scan = new Scanner (System.in);        int a = Integer.parseInt(scan.nextLine());        int b = Integer.parseInt(scan.nextLine());        while (b != 0) {            int oldb = b;            b = a % b;            a = oldb;        }        System.out.println("GCD = "+ a);    }}