package app;        import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String FirstName = scanner.nextLine();        String LastName = scanner.nextLine();        if (FirstName.isEmpty()) {            FirstName = "*****";        }        if (LastName.isEmpty()) {            LastName = "*****";        }        System.out.printf("Hello, " + FirstName + " " + LastName + "!");    }}