package com.company;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String A1 = scanner.nextLine();        String a1 = scanner.nextLine();        String b1 = scanner.nextLine();        String c1 = scanner.nextLine();        int n = Integer.parseInt(scanner.nextLine());        int sum = 0;        String all = A1 + a1 + b1 + c1 + n;        char A = all.charAt(0);        char a = all.charAt(1);        char b = all.charAt(2);        char c = all.charAt(3);        for (char i = 'A'; i <= A; i++) {            for (char j = 'a'; j <= a; j++) {                for (char k = 'a'; k <= b; k++) {                    for (char l = 'a'; l <= c; l++) {                        for (int m = 0; m <= n; m++) {                            if (i == A && j == a && k == b && l == c && m == n) {                                break;                            }                            sum++;                        }                    }                }            }        }        System.out.println(sum);    }}