package com.company;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String month = scanner.nextLine();        int nights = Integer.parseInt(scanner.nextLine());        double studio = 0.00;        double apartment = 0.00;        double studioPrice = 0.00;        double apartmentPrice = 0.00;        switch (month) {            case "May":            case "October":            studio = 50.00;            apartment = 65.00;                if (nights > 7 && nights <= 14) {                    studio = studio - studio * 0.05;                } else if (nights > 14) {                    studio = studio - studio * 0.3;                    apartment = apartment - apartment * 0.1;                }                studioPrice = studio * nights;                apartmentPrice = apartment * nights;            break;            case "June":            case "September":            studio = 75.20;            apartment = 68.70;                if (nights > 14) {                    studio = studio - studio * 0.2;                    apartment = apartment - apartment * 0.1;                }                studioPrice = studio * nights;                apartmentPrice = apartment * nights;            break;            case "July":            case "August":                studio = 76.00;                apartment = 77.00;                if (nights > 14) {                    apartment = apartment - apartment * 0.1;                }                studioPrice = studio * nights;                apartmentPrice = apartment * nights;            break;        default:            break;        }        System.out.printf("Apartment: %.2f lv.%n", apartmentPrice);        System.out.printf("Studio: %.2f lv.", studioPrice);    }}