package app;        import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        double x = Double.parseDouble(scanner.nextLine());        double y = Double.parseDouble(scanner.nextLine());        double h = Double.parseDouble(scanner.nextLine());        double wall = x * y;        double window = 1.5 * 1.5;        double steni = 2 * wall - 2 * window;        double backwall = x * x;        double entrance = 2 * 1.2;        double wallall = 2 * backwall - entrance;        double totalwall = steni + wallall;        double pokriv1 = 2 * x * y;        double pokriv2 = 2 * (x * h / 2);        double pokrivall = pokriv1 + pokriv2;        double green = totalwall / 3.4;        double red = pokrivall / 4.3;        System.out.printf("%.2f%n%.2f", green, red);    }}