package com.company;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int n = Integer.parseInt(scanner.nextLine());        int start = 1;        if (n % 2 == 0) {            start++;        }        for (int r = 0; r < (n + 1) / 2; r++) {            int dashCount = (n - start) / 2;            String line = repeatStr("-", dashCount) + repeatStr("*", start) + repeatStr("-", dashCount);            System.out.println(line);            start += 2;        }        for (int r = 0; r < n / 2; r++) {            String line = "|" + repeatStr("*", n - 2) + "|";            System.out.println(line);        }    }    private static String repeatStr(String str, int count) {        StringBuilder sb = new StringBuilder();        for (int i = 0; i < count; i++) {            sb.append(str);        }        return sb.toString();    }}