package com.company;        import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int n = Integer.parseInt(scanner.nextLine());        int difference = 0;        int leftsum = 0;        int rightsum = 0;        for (int i = 1; i <= n ; i++) {            leftsum = leftsum + scanner.nextInt();        }        for (int i = 1; i <= n ; i++) {            rightsum = rightsum + scanner.nextInt();        }        difference = Math.abs(leftsum - rightsum);        if (difference == 0) {            System.out.println("Yes, sum = " + rightsum );        } else {            System.out.println("No, diff = " + difference );        }    }}