package tasks;import java.util.Scanner;public class Task_01 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int n = Integer.parseInt(scanner.nextLine());        int sum = 0;        int maxnumber = Integer.MIN_VALUE;        for (int i = 1; i <= n ; i++) {            int num = Integer.parseInt(scanner.nextLine());            sum = sum + num;            if (num > maxnumber) {                maxnumber = num;            }        }        if (maxnumber == sum - maxnumber) {            System.out.println("Yes");            System.out.println("Sum = " + maxnumber);        } else {            System.out.println("No");            System.out.println("Diff = " + Math.abs(sum - maxnumber - maxnumber));        }    }}