package app;public class Main {    public static void main(String[] args) {        int m = 10;        for (int i = 1; i <= m; i++) {            for (int j = i; j <= m; j++) {                for (int k = j; k <= m; k++) {                    if (i != j && i != k && j != k) {                        System.out.printf("%d %d %d%n", i, j, k);                    }                }            }        }    }}