package com.company;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        double Budget = Double.parseDouble(scanner.nextLine());        String Category = scanner.nextLine();        int TotalFans = Integer.parseInt(scanner.nextLine());        double money = 0.00;        double razhod = 0.00;        if (Category.equals("Normal")) {            money = 249.99;        } else if (Category.equals("VIP")) {            money = 499.99;        }        if (TotalFans >= 1 && TotalFans <= 4) {            razhod = Budget * 0.75;        } else if (TotalFans >= 5 && TotalFans <= 9) {            razhod = Budget * 0.6;        } else if (TotalFans >= 10 && TotalFans <= 24) {            razhod = Budget * 0.5;        } else if (TotalFans >= 25 && TotalFans <= 49) {            razhod = Budget * 0.4;        } else if (TotalFans >= 50) {            razhod = Budget * 0.25;        }        double Rest = Budget - razhod;        if (money * TotalFans <= Rest) {            System.out.printf("Yes! You have %.02f leva left.", Rest - money * TotalFans);        } else{            System.out.printf("Not enough money! You need %.02f leva.", money * TotalFans - Rest);        }    }}