package com.company;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int n = Integer.parseInt(scanner.nextLine());        int maxnumber = Integer.MIN_VALUE;        for (int i = 1; i <= n ; i++) {            int num = Integer.parseInt(scanner.nextLine());            if (maxnumber < num){                maxnumber = num;            }        }        System.out.println(maxnumber);    }}