package app;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int n = Integer.parseInt(scanner.nextLine());        double PrizePerPoint = Double.parseDouble(scanner.nextLine());        double prize = 1;        double total = 0;        for (int i = 1; i <= n; i++) {            int p = Integer.parseInt(scanner.nextLine());            if (i % 2 == 0) {                prize = p * 2;                } else {                prize = p;            }            total = total + prize * PrizePerPoint;        }        System.out.printf("The project prize was %.2f lv.", total);    }}