package app;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scan = new Scanner (System.in);        int n = Integer.parseInt(scan.nextLine());        int num = 1;        for (int row = 0; row < n; row ++) {            for (int col = 0; col < n; col++) {                num = row + col + 1;                if (num > n) {                    num = 2 * n - num;                }                System.out.print(num + " ");            }            System.out.println();        }    }}