package com.company;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int n = Integer.parseInt(scanner.nextLine());        int difference = 0;        int oddsum = 0;        int evensum = 0;        for (int i = 1; i <= n ; i++) {            int num = Integer.parseInt(scanner.nextLine());            if (i % 2 == 0) {                evensum = evensum + num;            } else {                oddsum = oddsum + num;            }        }        difference = Math.abs(oddsum - evensum);        if (difference == 0) {            System.out.println("Yes");            System.out.println("Sum = " + oddsum );        } else {            System.out.println("No");            System.out.println("Diff = " + difference );        }    }}