package app;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int n1 = Integer.parseInt(scanner.nextLine());        int n2 = Integer.parseInt(scanner.nextLine());        String operator = scanner.nextLine();        int sabirane = n1 + n2;        int izvajdane = n1 - n2;        int umnojenie = n1 * n2;        if (operator.equals("+")) {            if (sabirane % 2 == 0) {                System.out.printf("%d %s %d = %d - even", n1, operator, n2, sabirane);            } else {                System.out.printf("%d %s %d = %d - odd", n1, operator, n2, sabirane);            }        } else if (operator.equals("-")) {            if (izvajdane % 2 == 0) {                System.out.printf("%d %s %d = %d - even", n1, operator, n2, izvajdane);            } else {                System.out.printf("%d %s %d = %d - odd", n1, operator, n2, izvajdane);            }        } else if (operator.equals("*")) {            if (umnojenie % 2 == 0) {                System.out.printf("%d %s %d = %d - even", n1, operator, n2, umnojenie);            } else {                System.out.printf("%d %s %d = %d - odd", n1, operator, n2, umnojenie);            }        }        if (n2 != 0) {            double delenie = (double) n1 / n2;            int delenie2 = n1 % n2;            if (operator.equals("/")) {                System.out.printf("%d / %d = %.2f", n1, n2, delenie);            } else if (operator.equals("%")) {                System.out.printf("%d %s %d = %d", n1, operator, n2, delenie2);            }        }else {            System.out.printf("Cannot divide %d by zero", n1);        }    }}