package app;import java.math.BigInteger;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int n = scanner.nextInt();        int m = scanner.nextInt();        int i = n;        BigInteger sum = BigInteger.valueof(1);        do {            sum = sum.multiply(BigInteger.valueOf(i++));;        }        while (i <= m);        System.out.printf("product[%d..%d] = %d", n, m, sum);    }}