package com.company;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int n = Integer.parseInt(scanner.nextLine());        for (int row = 1; row <= n; row++)        {            for (int space = 1; space <= n - row; space++)            {                System.out.print(" ");            }            System.out.print("*");            for (int star = 1; star < row; star++)            {                System.out.print(" *");            }            System.out.println();        }        for (int row = n - 1; row > 0; row--)        {            for (int star = 1; star <= n - row; star++)            {                System.out.print(" ");            }            System.out.print("*");            for (int space = 1; space < row; space++)            {                System.out.print(" *");            }            System.out.println();        }    }}