package app;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int n = Integer.parseInt(scanner.nextLine());        double Peralnia = Double.parseDouble(scanner.nextLine());        int GamePrice = Integer.parseInt(scanner.nextLine());        double sumeven = 0;        int counter1 = 0;        double sumodd = GamePrice;        int counter2 = 0;        for (int i = 1; i <= n; i++) {            if (i % 2 == 0) {                counter1++;                sumeven = sumeven + ((counter1 * 10) - 1);            } else {                counter2++;            }        }        sumodd = counter2 * GamePrice;        double spesteni = sumeven + sumodd;        if (spesteni >= Peralnia) {            double ostatak1 = spesteni - Peralnia;            System.out.printf("Yes! %.2f", ostatak1);        } else {            double ostatak2 = Peralnia - spesteni;            System.out.printf("No! %.2f", ostatak2);        }    }}