package com.company;        import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int n = Integer.parseInt(scanner.nextLine());        System.out.print("+ ");        for (int c = 1; c < n - 1; c++) {            System.out.print("- ");        }        System.out.println("+");        for (int c = 1; c < n - 1; c++) {            System.out.print("| ");            for (int i = 1; i < n - 1; i++) {                System.out.print("- ");            }            System.out.println("|");        }        System.out.print("+ ");        for (int c = 1; c < n - 1; c++) {            System.out.print("- ");        }        System.out.println("+");    }}