package app;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scan = new Scanner (System.in);        int n = Integer.parseInt(scan.nextLine());        System.out.println(repeatStr(".", n + 1) + repeatStr("_", 2 * n + 1)                + repeatStr(".", n +1));        for (int i = 0; i <= n - 1; i++) {            System.out.println(repeatStr(".", n - i) + "//" + repeatStr("_", 2 * n + 2 * i -1)                    + "\\\\" + repeatStr(".", n - i));        }        System.out.println("//" + repeatStr("_", n + n - 3) + "STOP!"                + repeatStr("_", n + n - 3) + "\\\\");        for (int i = n; i >= 1; i--) {            System.out.println(repeatStr(".", n - i) + "\\\\" + repeatStr("_", 2 * n + 2 * i -1)                    + "//" + repeatStr(".", n - i));        }    }       private static String repeatStr(String str, int count) {        StringBuilder sb = new StringBuilder();        for (int i = 0; i < count; i++) {            sb.append(str);        }        return sb.toString();    }}