package app;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int n = Integer.parseInt(scanner.nextLine());        boolean NO = false;        for (int a = 1; a <= 30; a++) {            for (int b = 1; b <= 30; b++) {                for (int c = 1; c <= 30; c++) {                    if ((a < b && b < c) && (n == a + b + c)) {                        System.out.printf("%d + %d + %d = %d", a, b, c, n);                        System.out.println();                        NO = true;                    }                    else if ((a > b && b > c) && (n == a * b * c)) {                        System.out.printf("%d * %d * %d = %d", a, b, c, n);                        System.out.println();                        NO = true;                    }                }            }        }        if (!NO) {            System.out.println("No!");        }    }}