package app;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int n = Integer.parseInt(scanner.nextLine());        double budget = Double.parseDouble(scanner.nextLine());        double salary = budget / n;        double JelevSalary = 0.0;        double RoYaLSalary = 0.0;        double RoliSalary = 0.0;        double TrofonSalary = 0.0;        double SinoSalary = 0.0;        double OthersSalary = 0.0;        for (int i = 1; i <= n; i++) {            String lector = scanner.nextLine();            switch (lector) {                case "Jelev":                    JelevSalary = JelevSalary + salary;                    break;                case "RoYaL":                    RoYaLSalary = RoYaLSalary + salary;                    break;                case "Roli":                    RoliSalary = RoliSalary + salary;                    break;                case "Trofon":                    TrofonSalary = TrofonSalary + salary;                    break;                case "Sino":                    SinoSalary = SinoSalary + salary;                    break;                default:                    OthersSalary = OthersSalary + salary;                    break;            }        } System.out.printf("Jelev salary: %.2f lv%n", JelevSalary);        System.out.printf("RoYaL salary: %.2f lv%n", RoYaLSalary);        System.out.printf("Roli salary: %.2f lv%n", RoliSalary);        System.out.printf("Trofon salary: %.2f lv%n", TrofonSalary);        System.out.printf("Sino salary: %.2f lv%n", SinoSalary);        System.out.printf("Others salary: %.2f lv", OthersSalary);    }}