package com.company;        import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int n = Integer.parseInt(scanner.nextLine());        for (int r = 1; r <= n; r++) {            System.out.print("$");            for (int c = 1; c < r; c++) {                System.out.print(" $");            }            System.out.println();        }    }}