package com.company;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int begin = Integer.parseInt(scanner.nextLine());        int end = Integer.parseInt(scanner.nextLine());        int magic = Integer.parseInt(scanner.nextLine());        int counter = 0;        boolean result = false;        for (int i = begin; i >= end; i--) {            for (int j = begin; j >= end; j--) {                counter++;                if (magic == i + j) {                    result = true;                    System.out.printf("Combination N:%d (%d + %d = %d)", counter, i, j, magic);                    System.out.println();                    break;                }            }            if (result) {                break;            }        }        if (!result) {            System.out.printf("%d combinations - neither equals %d", counter, magic);        }    }}