package com.company;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String alpha = scanner.nextLine().toLowerCase();        int sum = 0;        for (int i = 0; i < alpha.length(); i++) {            if (alpha.charAt(i) == 'а') {                sum += 1;            } else if (alpha.charAt(i) == 'ъ') {                sum += 2;            } else if (alpha.charAt(i) == 'о') {                sum += 3;            } else if (alpha.charAt(i) == 'у') {                sum += 4;            } else if (alpha.charAt(i) == 'е') {                sum += 5;            } else if (alpha.charAt(i) == 'и') {                sum += 6;            }        }        System.out.println(sum);    }}