package app;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        double record = Double.parseDouble(scanner.nextLine());        double distance = Double.parseDouble(scanner.nextLine());        double time = Double.parseDouble(scanner.nextLine());        double result = distance * time;        int IvanchoResult = (int)(distance / 15);        double i = IvanchoResult * 12.5;        double sum = result + i;        if (record > sum ) {            System.out.printf("Yes, he succeeded! The new world record is %.2f seconds.", sum);        } else {            System.out.printf("No, he failed! He was %.2f seconds slower.", (sum - record));        }    }}